import React, { useState, useRef, useEffect } from 'react';
import { analyzeVideo, VideoAnalysisResult } from '../visionModelIntegration';

interface VideoData {
  id: string;
  name: string;
  path: string;
  timestamp: number;
  isVideo: boolean;
}

const Home: React.FC = () => {
  const [uploadedVideo, setUploadedVideo] = useState<VideoData | null>(null);
  const [analysisResults, setAnalysisResults] = useState<VideoAnalysisResult[] | null>(null);
  const videoRef = useRef<HTMLVideoElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);

  const handleFileChange = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const selectedFile = event.target.files?.[0];
    if (selectedFile) {
      const formData = new FormData();
      formData.append('file', selectedFile);

      try {
        const response = await fetch('/api/upload', {
          method: 'POST',
          body: formData,
        });

        if (response.ok) {
          const data: VideoData = await response.json();
          setUploadedVideo(data);
          alert('File uploaded successfully!');

          // If the uploaded file is a video, kick off the analysis.
          if (data.isVideo && selectedFile.type === 'video/mp4') {
            // Call analyzeVideo which returns an array of predictions (with timestamps)
            const results = await analyzeVideo(selectedFile);
            setAnalysisResults(results);
          }
        } else {
          alert('Upload failed.');
        }
      } catch (err) {
        console.error('Error uploading file:', err);
      }
    }
  };

  // Update canvas overlay whenever the video's time changes.
  useEffect(() => {
    const videoEl = videoRef.current;
    const canvasEl = canvasRef.current;
    if (!videoEl || !canvasEl || !analysisResults) return;

    const context = canvasEl.getContext('2d');
    if (!context) return;

    const drawOverlay = () => {
      // Clear canvas
      context.clearRect(0, 0, canvasEl.width, canvasEl.height);

      // Find prediction near the current timestamp (adjust threshold as needed)
      const currentTime = videoEl.currentTime;
      const result = analysisResults.find(
        (r) => Math.abs(r.timestamp - currentTime) < 0.5
      );
      if (result && result.prediction && result.prediction.predictions) {
        result.prediction.predictions.forEach((pred: any) => {
          // Assume pred has x, y, width, height, label fields.
          context.strokeStyle = 'red';
          context.lineWidth = 2;
          context.strokeRect(pred.x, pred.y, pred.width, pred.height);
          context.font = '16px Arial';
          context.fillStyle = 'red';
          context.fillText(pred.label, pred.x, pred.y - 5);
        });
      }
    };

    videoEl.addEventListener('timeupdate', drawOverlay);

    // Adjust canvas size whenever video metadata loads.
    const handleLoadedMetadata = () => {
      canvasEl.width = videoEl.videoWidth;
      canvasEl.height = videoEl.videoHeight;
    };
    videoEl.addEventListener('loadedmetadata', handleLoadedMetadata);

    return () => {
      videoEl.removeEventListener('timeupdate', drawOverlay);
      videoEl.removeEventListener('loadedmetadata', handleLoadedMetadata);
    };
  }, [analysisResults]);

  return (
    <div className="home-container">
      <h2>Upload and View on Home Page</h2>
      <div className="upload-section">
        <label htmlFor="file-input" className="file-upload-label">
          Choose File
        </label>
        <input
          id="file-input"
          type="file"
          accept="image/*,video/*"
          onChange={handleFileChange}
          className="hidden-file-input"
        />
      </div>

      {/* Show newly uploaded video or image immediately */}
      {uploadedVideo && (
        <div style={{ marginTop: '20px', position: 'relative', display: 'inline-block' }}>
          <h3>{uploadedVideo.name}</h3>
          {uploadedVideo.isVideo ? (
            <>
              <video ref={videoRef} width="400" controls>
                <source src={uploadedVideo.path} type="video/mp4" />
                Your browser does not support the video tag.
              </video>
              {/* Canvas overlay on top of the video */}
              <canvas
                ref={canvasRef}
                style={{
                  position: 'absolute',
                  top: 0,
                  left: 0,
                  pointerEvents: 'none'
                }}
              />
            </>
          ) : (
            <img src={uploadedVideo.path} alt={uploadedVideo.name} width="400" />
          )}
        </div>
      )}
    </div>
  );
};

export default Home;
