import React, { useState } from 'react';

const Home: React.FC = () => {
    const [file, setFile] = useState<File | null>(null);
    const [playProbability, setPlayProbability] = useState<number | null>(null);
    const [isVideo, setIsVideo] = useState<boolean>(false);

    const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        const selectedFile = event.target.files?.[0];
        if (selectedFile) {
            setFile(selectedFile);
            setIsVideo(selectedFile.type.startsWith('video/'));
            setPlayProbability(null); // Reset probability on new file
        }
    };

    const handleScan = async () => {
        if (!file) return;

        const formData = new FormData();
        formData.append('file', file);

        const response = await fetch('/api/scan', {
            method: 'POST',
            body: formData,
        });

        if (response.ok) {
            const data = await response.json();
            setPlayProbability(data.probability);
        } else {
            console.error('Error scanning the file');
        }
    };

    return (
        <div className="home-container">
            <h1>BoilerMake Football Plays</h1>
            <div className="upload-section">
                <label htmlFor="file-input" className="file-upload-label">
                    Upload File
                </label>
                <input
                    id="file-input"
                    type="file"
                    accept="image/*,video/*"
                    onChange={handleFileChange}
                    style={{ display: 'none' }}
                />
            </div>
            <button onClick={handleScan} disabled={!file}>Scan</button>

            {isVideo ? (
                file && (
                <video controls width="600">
                    <source src={URL.createObjectURL(file)} type={file.type} />
                    Your browser does not support the video tag.
                </video>
                )
            ) : (
                file && <img src={URL.createObjectURL(file)} alt="Uploaded" width="600" />
            )}

            {playProbability !== null && (
                <div className="probability-display">
                    <h2>Predicted Play Probability: {playProbability.toFixed(2)}%</h2>
                </div>
            )}
        </div>
    );
};

export default Home;
