import React, { useEffect, useState } from 'react';

interface StoredVideo {
    id: string;
    name: string;
    dataUrl: string;
    timestamp: number;
    isVideo: boolean;
}

const Library: React.FC = () => {
    const [videos, setVideos] = useState<StoredVideo[]>([]);

    useEffect(() => {
        const stored = localStorage.getItem('uploadedVideos');
        if (stored) {
            const parsed = JSON.parse(stored) as StoredVideo[];
            // Sort newest first
            parsed.sort((a, b) => b.timestamp - a.timestamp);
            setVideos(parsed);
        }
    }, []);

    return (
        <div className="home-container">
            <h2>Video Library</h2>
            <p>This page displays preloaded and user-uploaded videos. Most recent appear first.</p>

            {videos.map((vid) => (
                <div key={vid.id} style={{ marginBottom: '2rem' }}>
                    <h3>{vid.name}</h3>
                    {vid.isVideo ? (
                        <video controls width="600">
                            <source src={vid.dataUrl} type="video/mp4" />
                            Your browser does not support the video tag.
                        </video>
                    ) : (
                        <img src={vid.dataUrl} alt={vid.name} width="600" />
                    )}
                </div>
            ))}
        </div>
    );
};

export default Library;
